import React, { Component } from 'react'
import './home.css'
import axios from 'axios'



export class home extends Component {
    constructor(props) {
        super(props)
        this.state = {
             search : '',
             output : []
        }
    }
    changeHandler = (e) => {
        this.setState({[e.target.name]:e.target.value})
    }
    searchValue=()=>{
        axios.get('http://localhost:1200/search?search=' + this.state.search).then(response => {
            this.setState({
              output : response.data.info
            })

            console.log(this.state.output)
        }).catch(error => {
          console.log(error)
        })
    }
    redirect = (event) => {
        event.preventDefault();
        this.props.history.push("/movie",{result: output})

    }

    render() {
        const {search,output} = this.state
        return (
        <>
            <div id="total">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-6">
                            <form>
                                <div class="form-group">
                                    <input type="text" class="form-control" id="search" name = "search" value = {search} aria-describedby="emailHelp" placeholder="Search" onChange={this.changeHandler}></input>
                                </div>
                            </form>
                        </div>
                        <div class="col-lg-6">
                            <button onClick={this.searchValue} id="btn">Search</button>
                        </div>


                        <img src={output.Poster} onClick = {this.redirect}></img>

                    </div>
                </div>              
            </div>
        </>
        )
    }
}

export default home
