import React, { Component } from 'react'
import './home.css'
import axios from 'axios'


export class home extends Component {
    constructor(props) {
        super(props)

        this.state = {
             search : ''
        }
    }
    changeHandler = (e) => {
        this.setState({[e.target.name]:e.target.value})
    }
    searchValue=()=>{

        console.log(this.state.search)
        axios.post('http://localhost:1200/search' + this.state.search).then(response => {
            console.log(response)
        }).catch(error => {
          console.log(error)
        })
    }

    render() {
        const {search} = this.state
        return (
                <div id="total">
                    <div class="container">
                        <div class="row">
                            <div class="col-lg-6">
                                <form>
                                    <div class="form-group">
                                        <input type="text" class="form-control" id="search" name = "search" value = {search} aria-describedby="emailHelp" placeholder="Search" onChange={this.changeHandler}></input>
                                    </div>
                                </form>
                            </div>
                            <div class="col-lg-6">
                                <button onClick={this.searchValue} id="btn">Search</button>
                            </div>
                        </div>
                    </div>               





            </div>
        )
    }
}

export default home
